require('dotenv').config();
const { Client, GatewayIntentBits } = require('discord.js');
const { google } = require('googleapis');

// Discord client setup
const client = new Client({
    intents: [
        GatewayIntentBits.Guilds,
        GatewayIntentBits.GuildMessages,
        GatewayIntentBits.MessageContent
    ]
});

// Google Sheets setup
const SCOPES = ['https://www.googleapis.com/auth/spreadsheets'];
const SPREADSHEET_ID = process.env.SPREADSHEET_ID;

let sheets;

// Validation constants
const BRANDS = ['Genesis', 'AGI Inc', 'BetVikings', 'FOMO', 'PayOut'];
const PLATFORMS = ['TikTok', 'Instagram', 'X', 'YouTube', 'Facebook', 'Threads', 'LinkedIn'];

// Initialize Google Sheets API
async function initSheets() {
    const auth = new google.auth.GoogleAuth({
        keyFile: process.env.SERVICE_ACCOUNT_KEY_PATH,
        scopes: SCOPES,
    });
    const authClient = await auth.getClient();
    sheets = google.sheets({ version: 'v4', auth: authClient });
}

// Helper: Read from sheet
async function readSheet(sheetName, range) {
    try {
        const response = await sheets.spreadsheets.values.get({
            spreadsheetId: SPREADSHEET_ID,
            range: `${sheetName}!${range}`,
        });
        return response.data.values || [];
    } catch (error) {
        console.error('Error reading sheet:', error);
        throw error;
    }
}

// Helper: Append to sheet
async function appendToSheet(sheetName, values) {
    try {
        await sheets.spreadsheets.values.append({
            spreadsheetId: SPREADSHEET_ID,
            range: `${sheetName}!A:Z`,
            valueInputOption: 'USER_ENTERED',
            resource: { values: [values] },
        });
    } catch (error) {
        console.error('Error appending to sheet:', error);
        throw error;
    }
}

// Helper: Validate brand
function validateBrand(brand) {
    return BRANDS.some(b => b.toLowerCase() === brand.toLowerCase());
}

// Helper: Validate platform
function validatePlatform(platform) {
    return PLATFORMS.some(p => p.toLowerCase() === platform.toLowerCase());
}

// Helper: Normalize handle (ensure @ prefix)
function normalizeHandle(handle) {
    return handle.startsWith('@') ? handle : `@${handle}`;
}

// Helper: Get current date
function getCurrentDate() {
    return new Date().toISOString().split('T')[0];
}

// Helper: Get current timestamp
function getCurrentTimestamp() {
    return new Date().toISOString().replace('T', ' ').split('.')[0];
}

// Command: /addaccount
async function handleAddAccount(message, args) {
    if (args.length < 3) {
        return message.reply('‚ùå Usage: `/addaccount <handle> <brand> <platform>`');
    }

    const handle = normalizeHandle(args[0]);
    const brand = args[1];
    const platform = args[2];

    // Validate brand
    if (!validateBrand(brand)) {
        return message.reply(`‚ùå Invalid brand. Choose from: ${BRANDS.join(', ')}`);
    }

    // Validate platform
    if (!validatePlatform(platform)) {
        return message.reply(`‚ùå Invalid platform. Choose from: ${PLATFORMS.join(', ')}`);
    }

    try {
        // Check if account already exists
        const accounts = await readSheet('accounts', 'A:G');
        const existing = accounts.find(row => 
            row[1]?.toLowerCase() === handle.toLowerCase() &&
            row[2]?.toLowerCase() === brand.toLowerCase() &&
            row[3]?.toLowerCase() === platform.toLowerCase()
        );

        if (existing) {
            return message.reply(`‚ùå This account already exists (ID: ${existing[0]})`);
        }

        // Get next account_id
        const accountId = accounts.length > 1 ? parseInt(accounts[accounts.length - 1][0]) + 1 : 1;

        // Add to sheet
        await appendToSheet('accounts', [
            accountId,
            handle,
            brand,
            platform,
            message.author.id,
            message.author.username,
            getCurrentDate()
        ]);

        message.reply(`‚úÖ Account added!\nHandle: ${handle}\nBrand: ${brand}\nPlatform: ${platform}\nAccount ID: ${accountId}`);
    } catch (error) {
        console.error('Error in addaccount:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Command: /submit
async function handleSubmit(message, args) {
    if (args.length < 3) {
        return message.reply('‚ùå Usage: `/submit <handle> <posts> <views>`');
    }

    const handle = normalizeHandle(args[0]);
    const posts = parseInt(args[1]);
    const views = parseInt(args[2]);

    // Validate posts
    if (isNaN(posts) || posts < 0 || posts > 1000) {
        return message.reply('‚ùå Posts must be a number between 0 and 1000');
    }

    // Validate views
    if (isNaN(views) || views < 0 || views > 100000000) {
        return message.reply('‚ùå Views must be a number between 0 and 100,000,000');
    }

    try {
        // Look up account
        const accounts = await readSheet('accounts', 'A:G');
        const account = accounts.find(row => row[1]?.toLowerCase() === handle.toLowerCase());

        if (!account) {
            return message.reply(`‚ùå Account ${handle} not found. Register it first with /addaccount`);
        }

        const accountId = account[0];
        const brand = account[2];
        const platform = account[3];

        // Get next submission_id
        const submissions = await readSheet('submissions', 'A:J');
        const submissionId = submissions.length > 1 ? parseInt(submissions[submissions.length - 1][0]) + 1 : 1;

        // Add to submissions sheet
        await appendToSheet('submissions', [
            submissionId,
            accountId,
            handle,
            brand,
            platform,
            posts,
            views,
            getCurrentDate(),
            message.author.id,
            getCurrentTimestamp()
        ]);

        message.reply(`‚úÖ Submission recorded!\nHandle: ${handle}\nBrand: ${brand}\nPlatform: ${platform}\nPosts: ${posts}\nViews: ${views.toLocaleString()}\nDate: ${getCurrentDate()}`);
    } catch (error) {
        console.error('Error in submit:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Command: /myaccounts
async function handleMyAccounts(message) {
    try {
        const accounts = await readSheet('accounts', 'A:G');
        const userAccounts = accounts.filter(row => row[4] === message.author.id);

        if (userAccounts.length === 0) {
            return message.reply('üìã You have no registered accounts.\nUse `/addaccount <handle> <brand> <platform>` to add one.');
        }

        let response = 'üìã Your Accounts:\n\n';
        userAccounts.forEach((acc, index) => {
            response += `${index + 1}. ${acc[1]} (${acc[2]} - ${acc[3]})\n`;
        });
        response += `\nTotal: ${userAccounts.length} account${userAccounts.length > 1 ? 's' : ''}`;

        message.reply(response);
    } catch (error) {
        console.error('Error in myaccounts:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Command: /dashboard
async function handleDashboard(message, args) {
    try {
        const accounts = await readSheet('accounts', 'A:G');
        const submissions = await readSheet('submissions', 'A:J');

        if (args.length === 0) {
            // User's dashboard
            const userAccounts = accounts.filter(row => row[4] === message.author.id);
            const userAccountIds = userAccounts.map(acc => acc[0]);
            const userSubmissions = submissions.filter(row => userAccountIds.includes(row[1]));

            if (userAccounts.length === 0) {
                return message.reply('üìä You have no registered accounts.');
            }

            const totalPosts = userSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const totalViews = userSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            // Last 7 days
            const sevenDaysAgo = new Date();
            sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
            const recentSubmissions = userSubmissions.filter(row => new Date(row[7]) >= sevenDaysAgo);
            const recentPosts = recentSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const recentViews = recentSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);
            const avgViews = recentPosts > 0 ? Math.round(recentViews / recentPosts) : 0;

            let response = `üìä Your Dashboard\n\nTotal Accounts: ${userAccounts.length}\nTotal Posts (all time): ${totalPosts}\nTotal Views (all time): ${totalViews.toLocaleString()}\n\nLast 7 Days:\n- Posts: ${recentPosts}\n- Views: ${recentViews.toLocaleString()}\n- Avg views/post: ${avgViews.toLocaleString()}`;

            message.reply(response);
        } else if (args[0].startsWith('@')) {
            // Handle-specific dashboard
            const handle = normalizeHandle(args[0]);
            const account = accounts.find(row => row[1]?.toLowerCase() === handle.toLowerCase());

            if (!account) {
                return message.reply(`‚ùå Account ${handle} not found.`);
            }

            const accountId = account[0];
            const accountSubmissions = submissions.filter(row => row[1] === accountId);

            const totalPosts = accountSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const totalViews = accountSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);
            const avgViews = totalPosts > 0 ? Math.round(totalViews / totalPosts) : 0;

            // Last 7 days
            const sevenDaysAgo = new Date();
            sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
            const recent7 = accountSubmissions.filter(row => new Date(row[7]) >= sevenDaysAgo);
            const posts7 = recent7.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const views7 = recent7.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            // Last 30 days
            const thirtyDaysAgo = new Date();
            thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
            const recent30 = accountSubmissions.filter(row => new Date(row[7]) >= thirtyDaysAgo);
            const posts30 = recent30.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const views30 = recent30.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            let response = `üìä Stats for ${handle} (${account[2]} - ${account[3]})\n\nAll Time:\n- Posts: ${totalPosts}\n- Views: ${totalViews.toLocaleString()}\n- Avg views/post: ${avgViews.toLocaleString()}\n\nLast 7 Days:\n- Posts: ${posts7}\n- Views: ${views7.toLocaleString()}\n\nLast 30 Days:\n- Posts: ${posts30}\n- Views: ${views30.toLocaleString()}`;

            message.reply(response);
        } else {
            // Brand dashboard
            const brand = args[0];
            const brandAccounts = accounts.filter(row => row[2]?.toLowerCase() === brand.toLowerCase());

            if (brandAccounts.length === 0) {
                return message.reply(`‚ùå No accounts found for brand: ${brand}`);
            }

            const brandAccountIds = brandAccounts.map(acc => acc[0]);
            const brandSubmissions = submissions.filter(row => brandAccountIds.includes(row[1]));

            const totalPosts = brandSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const totalViews = brandSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            // Last 7 days
            const sevenDaysAgo = new Date();
            sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
            const recent = brandSubmissions.filter(row => new Date(row[7]) >= sevenDaysAgo);
            const recentPosts = recent.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const recentViews = recent.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            // Top creators
            const creatorStats = {};
            recent.forEach(row => {
                const handle = row[2];
                if (!creatorStats[handle]) creatorStats[handle] = 0;
                creatorStats[handle] += parseInt(row[6] || 0);
            });
            const topCreators = Object.entries(creatorStats)
                .sort((a, b) => b[1] - a[1])
                .slice(0, 3);

            let response = `üìä ${brand} Brand Dashboard\n\nActive Creators: ${brandAccounts.length}\nTotal Posts (all time): ${totalPosts}\nTotal Views (all time): ${totalViews.toLocaleString()}\n\nLast 7 Days:\n- Posts: ${recentPosts}\n- Views: ${recentViews.toLocaleString()}\n\nTop Creators This Week:`;

            topCreators.forEach((creator, index) => {
                response += `\n${index + 1}. ${creator[0]} - ${creator[1].toLocaleString()} views`;
            });

            message.reply(response);
        }
    } catch (error) {
        console.error('Error in dashboard:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Discord ready event
client.on('ready', () => {
    console.log(`‚úÖ Logged in as ${client.user.tag}`);
});

// Message handler
client.on('messageCreate', async (message) => {
    if (message.author.bot) return;

    const content = message.content.trim();

    if (content.startsWith('/addaccount')) {
        const args = content.split(/\s+/).slice(1);
        await handleAddAccount(message, args);
    } else if (content.startsWith('/submit')) {
        const args = content.split(/\s+/).slice(1);
        await handleSubmit(message, args);
    } else if (content.startsWith('/myaccounts')) {
        await handleMyAccounts(message);
    } else if (content.startsWith('/dashboard')) {
        const args = content.split(/\s+/).slice(1);
        await handleDashboard(message, args);
    }
});

// Initialize and start
(async () => {
    try {
        await initSheets();
        console.log('‚úÖ Google Sheets API initialized');

        await client.login(process.env.DISCORD_BOT_TOKEN);
    } catch (error) {
        console.error('‚ùå Startup error:', error);
        process.exit(1);
    }
})();

