require('dotenv').config();
const { Client, GatewayIntentBits, ActionRowBuilder, StringSelectMenuBuilder } = require('discord.js');
const { google } = require('googleapis');

// Discord client setup
const client = new Client({
    intents: [
        GatewayIntentBits.Guilds,
        GatewayIntentBits.GuildMessages,
        GatewayIntentBits.MessageContent
    ]
});

// Google Sheets setup
const SCOPES = ['https://www.googleapis.com/auth/spreadsheets'];
const SPREADSHEET_ID = process.env.SPREADSHEET_ID;

let sheets;

// Validation constants
const BRANDS = ['Genesis', 'AGI Inc', 'BetVikings', 'FOMO', 'PayOut'];
const PLATFORMS = ['TikTok', 'Instagram', 'X', 'YouTube', 'Facebook', 'Threads', 'LinkedIn'];

// Initialize Google Sheets API
async function initSheets() {
    const auth = new google.auth.GoogleAuth({
        keyFile: process.env.SERVICE_ACCOUNT_KEY_PATH,
        scopes: SCOPES,
    });
    const authClient = await auth.getClient();
    sheets = google.sheets({ version: 'v4', auth: authClient });
}

// Helper: Read from sheet
async function readSheet(sheetName, range) {
    try {
        const response = await sheets.spreadsheets.values.get({
            spreadsheetId: SPREADSHEET_ID,
            range: `${sheetName}!${range}`,
        });
        return response.data.values || [];
    } catch (error) {
        console.error('Error reading sheet:', error);
        throw error;
    }
}

// Helper: Append to sheet
async function appendToSheet(sheetName, values) {
    try {
        await sheets.spreadsheets.values.append({
            spreadsheetId: SPREADSHEET_ID,
            range: `${sheetName}!A:Z`,
            valueInputOption: 'USER_ENTERED',
            resource: { values: [values] },
        });
    } catch (error) {
        console.error('Error appending to sheet:', error);
        throw error;
    }
}

// Helper: Validate brand
function validateBrand(brand) {
    return BRANDS.some(b => b.toLowerCase() === brand.toLowerCase());
}

// Helper: Validate platform
function validatePlatform(platform) {
    return PLATFORMS.some(p => p.toLowerCase() === platform.toLowerCase());
}

// Helper: Normalize handle (ensure @ prefix)
function normalizeHandle(handle) {
    return handle.startsWith('@') ? handle : `@${handle}`;
}

// Helper: Get current date
function getCurrentDate() {
    return new Date().toISOString().split('T')[0];
}

// Helper: Get current timestamp
function getCurrentTimestamp() {
    return new Date().toISOString().replace('T', ' ').split('.')[0];
}

// Command: /addaccount
async function handleAddAccount(message, args) {
    if (args.length < 3) {
        return message.reply('‚ùå Usage: `/addaccount <handle> <brand> <platform>`');
    }

    const handle = normalizeHandle(args[0]);
    const brand = args[1];
    const platform = args[2];

    // Validate brand
    if (!validateBrand(brand)) {
        return message.reply(`‚ùå Invalid brand. Choose from: ${BRANDS.join(', ')}`);
    }

    // Validate platform
    if (!validatePlatform(platform)) {
        return message.reply(`‚ùå Invalid platform. Choose from: ${PLATFORMS.join(', ')}`);
    }

    try {
        // Check if account already exists
        const accounts = await readSheet('accounts', 'A:G');
        const existing = accounts.find(row => 
            row[1]?.toLowerCase() === handle.toLowerCase() &&
            row[2]?.toLowerCase() === brand.toLowerCase() &&
            row[3]?.toLowerCase() === platform.toLowerCase()
        );

        if (existing) {
            return message.reply(`‚ùå This account already exists (ID: ${existing[0]})`);
        }

        // Get next account_id
        const accountId = accounts.length > 1 ? parseInt(accounts[accounts.length - 1][0]) + 1 : 1;

        // Add to sheet
        await appendToSheet('accounts', [
            accountId,
            handle,
            brand,
            platform,
            message.author.id,
            message.author.username,
            getCurrentDate()
        ]);

        message.reply(`‚úÖ Account added!\nHandle: ${handle}\nBrand: ${brand}\nPlatform: ${platform}\nAccount ID: ${accountId}`);
    } catch (error) {
        console.error('Error in addaccount:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Command: /submit
async function handleSubmit(message, args) {
    if (args.length < 1) {
        return message.reply('‚ùå Usage: `/submit`');
    }

    const handle = normalizeHandle(args[0]);

    // Fetch user accounts
    const accounts = await readSheet('accounts', 'A:G');
    const userAccounts = accounts.filter(row => row[4] === message.author.id);

    // Create Select Menu
    const accountOptions = userAccounts.map(acc => ({ label: acc[1], value: acc[1] }));

    const selectMenu = new StringSelectMenuBuilder()
        .setCustomId('accountSelect')
        .setPlaceholder('Select Account')
        .addOptions(accountOptions);

    const row = new ActionRowBuilder().addComponents(selectMenu);

    await message.reply({ content: 'Select Account:', components: [row] });
}

// Command: /submitbatch
async function handleSubmitBatch(message) {
    // Fetch user accounts
    const accounts = await readSheet('accounts', 'A:G');
    const userAccounts = accounts.filter(row => row[4] === message.author.id);

    // Create Select Menu
    const accountOptions = userAccounts.map(acc => ({ label: acc[1], value: acc[1] }));

    const selectMenu = new StringSelectMenuBuilder()
        .setCustomId('batchAccountSelect')
        .setPlaceholder('Select Accounts')
        .addOptions(accountOptions);

    const row = new ActionRowBuilder().addComponents(selectMenu);

    await message.reply({ content: 'Select Accounts to submit:', components: [row] });
}

// Discord ready event
client.on('ready', () => {
    console.log(`‚úÖ Logged in as ${client.user.tag}`);
});

// Message handler
client.on('messageCreate', async (message) => {
    if (message.author.bot) return;

    const content = message.content.trim();

    if (content.startsWith('/addaccount')) {
        const args = content.split(/\s+/).slice(1);
        await handleAddAccount(message, args);
    } else if (content.startsWith('/submit')) {
        const args = content.split(/\s+/).slice(1);
        await handleSubmit(message, args);
    } else if (content.startsWith('/submitbatch')) {
        await handleSubmitBatch(message);
    }
});

// Initialize and start
(async () => {
    try {
        await initSheets();
        console.log('‚úÖ Google Sheets API initialized');

        await client.login(process.env.DISCORD_BOT_TOKEN);
    } catch (error) {
        console.error('‚ùå Startup error:', error);
        process.exit(1);
    }
})();

