        const account = accounts.find(row => row[1]?.toLowerCase() === handle.toLowerCase());

        if (!account) {
            return message.reply(`‚ùå Account ${handle} not found. Register it first with /addaccount`);
        }

        const accountId = account[0];
        const brand = account[2];
        const platform = account[3];

        // Get next submission_id
        const submissions = await readSheet('submissions', 'A:J');
        const submissionId = submissions.length > 1 ? parseInt(submissions[submissions.length - 1][0]) + 1 : 1;

        // Add to submissions sheet
        await appendToSheet('submissions', [
            submissionId,
            accountId,
            handle,
            brand,
            platform,
            posts,
            views,
            getCurrentDate(),
            message.author.id,
            getCurrentTimestamp()
        ]);

        message.reply(`‚úÖ Submission recorded!\nHandle: ${handle}\nBrand: ${brand}\nPlatform: ${platform}\nPosts: ${posts}\nViews: ${views.toLocaleString()}\nDate: ${getCurrentDate()}`);
    } catch (error) {
        console.error('Error in submit:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Command: /myaccounts
async function handleMyAccounts(message) {
    try {
        const accounts = await readSheet('accounts', 'A:G');
        const userAccounts = accounts.filter(row => row[4] === message.author.id);

        if (userAccounts.length === 0) {
            return message.reply('üìã You have no registered accounts.\nUse `/addaccount <handle> <brand> <platform>` to add one.');
        }

        let response = 'üìã Your Accounts:\n\n';
        userAccounts.forEach((acc, index) => {
            response += `${index + 1}. ${acc[1]} (${acc[2]} - ${acc[3]})\n`;
        });
        response += `\nTotal: ${userAccounts.length} account${userAccounts.length > 1 ? 's' : ''}`;

        message.reply(response);
    } catch (error) {
        console.error('Error in myaccounts:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Command: /dashboard
async function handleDashboard(message, args) {
    try {
        const accounts = await readSheet('accounts', 'A:G');
        const submissions = await readSheet('submissions', 'A:J');

        if (args.length === 0) {
            // User's dashboard
            const userAccounts = accounts.filter(row => row[4] === message.author.id);
            const userAccountIds = userAccounts.map(acc => acc[0]);
            const userSubmissions = submissions.filter(row => userAccountIds.includes(row[1]));

            if (userAccounts.length === 0) {
                return message.reply('üìä You have no registered accounts.');
            }

            const totalPosts = userSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const totalViews = userSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            // Last 7 days
            const sevenDaysAgo = new Date();
            sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
            const recentSubmissions = userSubmissions.filter(row => new Date(row[7]) >= sevenDaysAgo);
            const recentPosts = recentSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const recentViews = recentSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);
            const avgViews = recentPosts > 0 ? Math.round(recentViews / recentPosts) : 0;

            let response = `üìä Your Dashboard\n\nTotal Accounts: ${userAccounts.length}\nTotal Posts (all time): ${totalPosts}\nTotal Views (all time): ${totalViews.toLocaleString()}\n\nLast 7 Days:\n- Posts: ${recentPosts}\n- Views: ${recentViews.toLocaleString()}\n- Avg views/post: ${avgViews.toLocaleString()}`;

            message.reply(response);
        } else if (args[0].startsWith('@')) {
            // Handle-specific dashboard
            const handle = normalizeHandle(args[0]);
            const account = accounts.find(row => row[1]?.toLowerCase() === handle.toLowerCase());

            if (!account) {
                return message.reply(`‚ùå Account ${handle} not found.`);
            }

            const accountId = account[0];
            const accountSubmissions = submissions.filter(row => row[1] === accountId);

            const totalPosts = accountSubmissions.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const totalViews = accountSubmissions.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);
            const avgViews = totalPosts > 0 ? Math.round(totalViews / totalPosts) : 0;

            // Last 7 days
            const sevenDaysAgo = new Date();
            sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
            const recent7 = accountSubmissions.filter(row => new Date(row[7]) >= sevenDaysAgo);
            const posts7 = recent7.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const views7 = recent7.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            // Last 30 days
            const thirtyDaysAgo = new Date();
            thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
            const recent30 = accountSubmissions.filter(row => new Date(row[7]) >= thirtyDaysAgo);
            const posts30 = recent30.reduce((sum, row) => sum + parseInt(row[5] || 0), 0);
            const views30 = recent30.reduce((sum, row) => sum + parseInt(row[6] || 0), 0);

            let response = `üìä Stats for ${handle} (${account[2]} - ${account[3]})\n\nAll Time:\n- Posts: ${totalPosts}\n- Views: ${totalViews.toLocaleString()}\n- Avg views/post: ${avgViews.toLocaleString()}\n\nLast 7 Days:\n- Posts: ${posts7}\n- Views: ${views7.toLocaleString()}\n\nLast 30 Days:\n- Posts: ${posts30}\n- Views: ${views30.toLocaleString()}`;

            message.reply(response);
        }
    } catch (error) {
        console.error('Error in dashboard:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

// Discord ready event
client.on('ready', () => {
    console.log(`‚úÖ Logged in as ${client.user.tag}`);
});

// Message handler
client.on('messageCreate', async (message) => {
    if (message.author.bot) return;

    const content = message.content.trim();

    if (content.startsWith('/tracker')) {
        handleTracker(message);
    } else if (content.startsWith('/addaccount')) {
        const args = content.split(/\s+/).slice(1);
        await handleAddAccount(message, args);
    } else if (content.startsWith('/submit')) {
        const args = content.split(/\s+/).slice(1);
        await handleSubmit(message, args);
    } else if (content.startsWith('/myaccounts')) {
        await handleMyAccounts(message);
    } else if (content.startsWith('/dashboard')) {
        const args = content.split(/\s+/).slice(1);
        await handleDashboard(message, args);
    }
});

