require('dotenv').config();
const { Client, GatewayIntentBits } = require('discord.js');
const { google } = require('googleapis');

// Discord client setup
const client = new Client({
    intents: [
        GatewayIntentBits.Guilds,
        GatewayIntentBits.GuildMessages,
        GatewayIntentBits.MessageContent
    ]
});

// Google Sheets setup
const SCOPES = ['https://www.googleapis.com/auth/spreadsheets'];
const SPREADSHEET_ID = process.env.SPREADSHEET_ID;

let sheets;

// Validation constants
const BRANDS = ['Genesis', 'AGI Inc', 'BetVikings', 'FOMO', 'PayOut'];
const PLATFORMS = ['TikTok', 'Instagram', 'X', 'YouTube', 'Facebook', 'Threads', 'LinkedIn'];

// Initialize Google Sheets API
async function initSheets() {
    const auth = new google.auth.GoogleAuth({
        keyFile: process.env.SERVICE_ACCOUNT_KEY_PATH,
        scopes: SCOPES,
    });
    const authClient = await auth.getClient();
    sheets = google.sheets({ version: 'v4', auth: authClient });
}

// Helper: Read from sheet
async function readSheet(sheetName, range) {
    try {
        const response = await sheets.spreadsheets.values.get({
            spreadsheetId: SPREADSHEET_ID,
            range: `${sheetName}!${range}`,
        });
        return response.data.values || [];
    } catch (error) {
        console.error('Error reading sheet:', error);
        throw error;
    }
}

// Helper: Append to sheet
async function appendToSheet(sheetName, values) {
    try {
        await sheets.spreadsheets.values.append({
            spreadsheetId: SPREADSHEET_ID,
            range: `${sheetName}!A:Z`,
            valueInputOption: 'USER_ENTERED',
            resource: { values: [values] },
        });
    } catch (error) {
        console.error('Error appending to sheet:', error);
        throw error;
    }
}

// Command: /dailytop
async function handleDailyTop(message, args) {
    if (args.length < 4) {
        return message.reply(`‚ùå **Invalid format.**\n\n**Correct usage:**\n\`/dailytop <link> <views> <brand> <usa%>\`\n\n**Example:**\n\`/dailytop https://tiktok.com/@user/video/123 50000 Genesis 65\``);
    }

    const videoLink = args[0];
    const views = parseInt(args[1]);
    const brand = args[2];
    const usaPercentage = parseInt(args[3]);

    // Validate inputs
    if (isNaN(views) || views < 0) {
        return message.reply('‚ùå Views must be a positive number.');
    }
    if (isNaN(usaPercentage) || usaPercentage < 0 || usaPercentage > 100) {
        return message.reply('‚ùå USA percentage must be between 0 and 100.');
    }

    const date = getCurrentDate();
    const creatorName = message.author.username;
    const creatorId = message.author.id;

    try {
        // Get next submission_id
        const submissions = await readSheet('dailytop', 'A:I');
        const submissionId = submissions.length > 1 ? parseInt(submissions[submissions.length - 1][0]) + 1 : 1;

        // Add to dailytop sheet
        await appendToSheet('dailytop', [
            submissionId,
            videoLink,
            views,
            brand,
            usaPercentage,
            date,
            creatorName,
            creatorId,
            getCurrentTimestamp()
        ]);

        message.reply(`‚úÖ Submission recorded!\nVideo Link: ${videoLink}\nViews: ${views}\nBrand: ${brand}\nUSA Percentage: ${usaPercentage}%\nDate: ${date}`);
    } catch (error) {
        console.error('Error in dailytop:', error);
        message.reply('‚ùå Database temporarily unavailable. Try again in a minute.');
    }
}

